components/f/category/show-amount:
{{#if category.total_products '>=' '12'}}
<div class="productCount js-product-count">
    <span class="productCount__show">{{lang 'category.show'}}</span>
    {{#if category.total_products '>=' '12'}}<a href="{{category.url}}?limit=12" class="productCount__item" data-count="12">12</a>{{/if}}
    {{#if category.total_products '>=' '24'}}<a href="{{category.url}}?limit=24" class="productCount__item" data-count="24">24</a>{{/if}}
    {{#if category.total_products '>=' '36'}}<a href="{{category.url}}?limit=36" class="productCount__item" data-count="36">36</a>{{/if}}
    {{#if category.total_products '>=' '48'}}<a href="{{category.url}}?limit=48" class="productCount__item" data-count="48">48</a>{{/if}}
    {{#if category.total_products '>=' '96'}}<a href="{{category.url}}?limit=96" class="productCount__item" data-count="96">96</a>{{/if}}
</div>
{{/if}}
